// C√¥ng th·ª©c Haversine: T√≠nh kho·∫£ng c√°ch gi·ªØa 2 t·ªça ƒë·ªô (m√©t)
function getDistance(lat1, lon1, lat2, lon2) {
    const R = 6371000; // B√°n k√≠nh Tr√°i ƒë·∫•t (m√©t)
    const phi1 = lat1 * Math.PI / 180;
    const phi2 = lat2 * Math.PI / 180;
